mapper.select:
select count(*) from u_board 


	where ${category} like '%'||#{searchWord}||'%' and u_btype=#{u_btype} 


	where u_btitle like '%'||#{searchWord}||'%' or u_bcontent like '%'||#{searchWord}||'%' and u_btype=#{u_btype}






	where ${category} like '%'||#{searchWord}||'%' and u_btype=#{u_btype} and u_bstatus= #{u_bstatus}


	where u_btitle like '%'||#{searchWord}||'%' or u_bcontent like '%'||#{searchWord}||'%' and u_btype=#{u_btype}and u_bstatus= #{u_bstatus}
